mapper.insert:
 INSERT INTO MP_MEMBER(   
                         usernum
                        ,USERID 
					       , USERPASS 
					       , USERNAME 	)
               VALUES(   
                         #{usernum}
                        , #{userid} 
              		   , #{userPass}
              		   , #{userName})

mapper.select:
SELECT USERID, USERPASS 
  FROM MP_MEMBER
 WHERE USERID = #{userid}
   AND USERPASS = #{userPass}

mapper.update:
UPDATE MP_MEMBER SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userid}

mapper.delete:
DELETE FROM MP_MEMBER
WHERE USERID = #{userid}
  AND USERPASS = #{userPass}

mapper.select:
select count(*) from mp_member
where userid=#{userid}
and userpass=#{userPass}
